import React from 'react';

const schedule = {
  "title": "CS Courses for 2018-2019",
  "courses": [
  {
    "id": "F101",
    "title": "Computer Science: Concepts, Philosophy, and Connections",
    "meets": "MWF 11:00-11:50" },

  {
    "id": "F110",
    "title": "Intro Programming for non-majors",
    "meets": "MWF 10:00-10:50" },

  {
    "id": "F111",
    "title": "Fundamentals of Computer Programming I",
    "meets": "MWF 13:00-13:50" },

  {
    "id": "W111",
    "title": "Fundamentals of Computer Programming I",
    "meets": "MWF 11:00-11:50" },

  {
    "id": "F211",
    "title": "Fundamentals of Computer Programming II",
    "meets": "TuTh 12:30-13:50" }] };




const terms = { F: 'Fall', W: 'Winter', S: 'Spring' };

const Banner = ({ title }) =>
React.createElement("h1", null, title);


const getCourseTerm = (course) =>
terms[course.id.charAt(0)];


const getCourseNumber = (course) =>
course.id.slice(1, 4);


const Course = ({ course }) =>
React.createElement("button", null, getCourseTerm(course), " CS ", getCourseNumber(course), ": ", course.title);


const CourseList = ({ courses }) =>
React.createElement("div", null,
courses.map(course => React.createElement(Course, { key: course.id, course: course })));



const App = () =>
React.createElement("div", null,
React.createElement(Banner, { title: schedule.title }),
React.createElement(CourseList, { courses: schedule.courses }));



ReactDOM.render(
React.createElement(App, null),
document.getElementById('container'));
